mapper.select:
select list.id, list.login_name, list.real_name, list.gender, list.genderName, list.phone_number, list.remark, list.is_locked, list.is_deleted, list.created_by, list.created_time, list.modified_by, list.modified_time, group_concat(list.sr_name) roleNameJoin, list.is_default from ( select su.id, su.login_name, su.real_name, su.gender, sc1.name genderName, su.phone_number, su.remark, su.is_locked, su.is_deleted, su.created_by, su.created_time, su.modified_by, su.modified_time, sr.name sr_name, su.is_default from sys_users su left join sys_user_in_role suir on suir.is_deleted = 0 and su.id = suir.user_id left join sys_roles sr on sr.is_deleted = 0 and sr.id = suir.role_id left join sys_code sc on sc.is_deleted = 0 and sr.type = sc.value left join sys_code sc1 on sc1.is_deleted = 0 and su.gender = sc1.value ) list
list.is_deleted = 0 and list.is_default = 0
and list.login_name like concat('%',#{userListDTO.loginName},'%')
and list.real_name like concat('%',#{userListDTO.realName},'%')
and list.created_time >= #{userListDTO.startTime}
and list.created_time <= #{userListDTO.endTime}
and list.created_time <= #{userListDTO.endTime}
group by list.id order by list.created_time desc
